import React, { Component } from "react";
import { ToDoItemRow } from "./ToDoItemRow";
import ToDoInput from "./ToDoInput";

class ToDoList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      toDoList: [
        { id: 1, description: "Get out of bed" , deleted:false},
        { id: 2, description: "Brush teeth" , deleted:false},
        { id: 3, description: "Eat breakfast" , deleted:false},
        { id: 4, description: "Go for a run" , deleted:false},
        { id: 5, description: "Buy grocery" , deleted:false},
        { id: 6, description: "Eat dinner" , deleted:false},
      ],
    };
    this.addItem = this.addItem.bind(this);
  }
  addItem() {
    const newItem = [
      ...this.state.toDoList,
      {
        id: this.state.toDoList.length + 1,
        description: Math.floor(Math.random() * (20 - 5 + 1)) + 5
      }
    ];
    this.setState({
      toDoList: newItem
    });
  }
  render() {
    const { toDoList } = this.state;
    const rows = this.state.toDoList.filter(todo=> todo.deleted === false).map((todo) => (
      <ToDoItemRow toDo={todo} key={todo.id} />
    ));
    return (
      <>
        <ToDoInput />
        <ul>{rows}</ul>
        <button onClick={this.addItem}>add</button>
      </>
    );
  }
}

export default ToDoList;
