import React, { Component } from "react";
import "../App.css";

export class ToDoItemRow extends Component {

  render() {
    const toDo = this.props.toDo;
    return (
      <div> 
        <li>
          <span className="checkmark">{toDo.description}</span> &nbsp;
          <input type="checkbox" onClick = {this.strikeItem}/> &nbsp;
          <button type="button">DELETE</button>
        </li>
      </div>
    );
  }

  strikeItem(){
      var checkboxElement = document.querySelector('.checkmark')
      if(checkboxElement.checked === true){
        checkboxElement.setAttribute("style", "text-decoration: line-through;");
      }
      else{
        checkboxElement.setAttribute("style", "text-decoration: line-through;");
      }
  }
}
