import React, { Component } from "react";
import "../App.css";

export class ToDoItemRow extends Component {
    constructor(props) {
        super(props);
        this.state = {todoItem:this.props.toDo};
        this.deleteItem = this.deleteItem.bind(this);
    }
  render() {
    const toDo = this.props.toDo;
    return (
      <div className = "list-no-bullets"> 
        <li>
          <input className ="checkbox" type="checkbox" /> &nbsp;
          <span className="todo">{this.state.todoItem.description}</span> &nbsp;
          <button className="button" type= "button" onClick={this.deleteItem}>DELETE</button>
        </li>
      </div>
    );
  }

  deleteItem(){
      const todo = this.state.todoItem;
    todo.deleted = true;
      this.setState({todoItem:todo});
  }
}
