import React, { Component } from "react";
import "../App.css";

export class ToDoItemRow extends Component {

  render() {
    const toDo = this.props.toDo;
    return (
      <div> 
        <li>
        <input className ="checkbox" type="checkbox" hidden/> &nbsp;
            <span className="todo">{toDo.description}</span> &nbsp;
          <input className ="checkbox" type="checkbox" /> &nbsp;
          <button type="button">DELETE</button>
        </li>
      </div>
    );
  }

  strikeItem(){
      const checkboxElement = document.querySelector('#checkboxId');
      const todoTextElement = document.querySelector(".checkmark");
      if(checkboxElement.checked === true){
        todoTextElement.setAttribute("style", "text-decoration: line-through;");
      }
      else{
        todoTextElement.setAttribute("style", "text-decoration: none;");
      }
  }
}
